import React from 'react'

const BoostItem = ({
	boost,
	handleBoostClick,
	userData,
	calculateTimeLeft,
	formatTimeLeft,
}) => {
	const timeLeft = calculateTimeLeft(
		boost.endTime,
		boost.startTime,
		boost.lastUsed,
		boost.usesToday,
		boost.level
	)

	return (
		<button
			key={boost.name}
			className='block day-boost'
			onClick={() =>
				handleBoostClick(boost.name, boost.icon, boost.boostType, boost.name)
			}
		>
			<div id='boost-info'>
				<h3 className='up-case'>
					{boost.name} {boost.level - boost.usesToday}
				</h3>
				<h3 className='up-case' id='timer'>
					{formatTimeLeft(timeLeft)}
				</h3>
			</div>
			<div className='icon'>
				{boost.name === 'turbo' &&
					(userData.stage === 1 ? (
						<img src={'/boosts/' + boost.icon} alt={boost.name} />
					) : (
						<img src={'/boosts/' + 'golden.svg'} alt={boost.name} />
					))}
				{boost.name !== 'turbo' && (
					<img src={'/boosts/' + boost.icon} alt={boost.name} />
				)}
			</div>
		</button>
	)
}

export default BoostItem
