import React, { useEffect, useState } from 'react'
import axiosDB from '../../../utils/axios/axiosConfig'
import Loading from '../Loading/Loading'
import './Tasks.css'

const Tasks = () => {
	const [taskData, setTaskData] = useState([])
	const [loading, setLoading] = useState(true)

	const telegramId = '1145622789'
	useEffect(() => {
		const getTasks = async () => {
			try {
				const tasksList = await axiosDB.get(`/tasks/${telegramId}`)
				setTaskData(tasksList.data.incompleteTasks)
				console.log('TASKDATA: ' + JSON.stringify(tasksList.data, null, 2))
			} catch (error) {
				console.log(error)
			} finally {
				setLoading(false)
			}
		}

		getTasks()
	}, [telegramId])

	if (loading) {
		return <Loading />
	}

	return (
		<div className='container'>
			<h1 className='main-title'>tasks</h1>
			<div className='tasks-content'>
				{taskData.map((task, index) => (
					<div key={index} id='task-container'>
						<div className='block tasks'>
							<div className='task'>
								<div id='task-block'>
									<div className='task-logo'></div>
									<div id='task-info'>
										<p id='name'>{task.name}</p>
										<p id='reward'>{task.reward}</p>
									</div>
								</div>
								<div className='row icon'></div>
							</div>
						</div>
					</div>
				))}
			</div>
			<Navigation />
		</div>
	)
}

export default Tasks
