import { useEffect } from 'react'
import goldenKey from '../../../../assets/pictures/keys/golden.svg'
import silverKey from '../../../../assets/pictures/keys/silver.svg'
import axiosDB from '../../../../utils/axios/axiosConfig'

const MainBalance = ({ stage, telegramId }) => {
	useEffect(() => {
		const fetchUserData = async () => {
			try {
				const response = await axiosDB.get(`/user/${telegramId}`)
				const user = response.data
				setCurrentEnergy(user.energy)
				setCurrentMaxEnergy(user.maxEnergy)
				setStage(user.stage)
				user.state === 1 ? setCoins(user.soldo) : setCoins(user.zecchino)
			} catch (error) {
				console.error('Error fetching user data:', error)
			}
		}

		fetchUserData()
	}, [telegramId])
	return (
		<div id='main-balance'>
			<h1 id='count'>4.046.100</h1>
			<div className='key'>
				<img
					draggable='false'
					src={stage === 1 ? silverKey : goldenKey}
					className='icon'
					alt=''
				/>
			</div>
		</div>
	)
}

export default MainBalance
