import React, { useCallback } from 'react'
import goldenCoin from '../../../assets/pictures/coins/golden/coin.svg'
import silverCoin from '../../../assets/pictures/coins/silver/coin.svg'
import axiosDB from '../../../utils/axios/axiosConfig'

const tg = window.Telegram.WebApp

const TapZone = ({
	telegramId,
	currentEnergy,
	setCurrentEnergy,
	energyReduction,
	stage,
	soldoTaps,
	setSoldoTaps,
	zecchinoTaps,
	setZecchinoTaps,
}) => {
	const handleTouchStart = useCallback(
		async e => {
			const touches = e.touches.length
			if (currentEnergy >= energyReduction) {
				const energySpent = energyReduction * touches
				const newEnergy = Math.max(0, currentEnergy - energySpent)
				setCurrentEnergy(newEnergy)

				const updatedSoldoTaps =
					stage === 1 ? soldoTaps + energySpent : soldoTaps
				const updatedZecchinoTaps =
					stage !== 1 ? zecchinoTaps + energySpent : zecchinoTaps

				if (stage === 1) {
					setSoldoTaps(updatedSoldoTaps)
				} else {
					setZecchinoTaps(updatedZecchinoTaps)
				}

				try {
					console.log('Sending PUT request to update user data')
					const response = await axiosDB.put('/user/update', {
						telegramId,
						energy: newEnergy,
						soldoTaps: updatedSoldoTaps,
						zecchinoTaps: updatedZecchinoTaps,
					})

					console.log('Response from server:', response.data)
					// Фактическое обновление состояния после успешного ответа
					setCurrentEnergy(response.data.energy)
					setSoldoTaps(response.data.soldoTaps)
					setZecchinoTaps(response.data.zecchinoTaps)
				} catch (error) {
					console.error('Error updating user:', error)
					console.error('Error details:', error.response?.data)
					// В случае ошибки можно добавить логику для отката изменений на клиенте
				}

				if (tg.HapticFeedback) {
					tg.HapticFeedback.impactOccurred('light')
				}
			} else {
				console.log('Not enough energy to perform the action')
			}
		},
		[
			telegramId,
			currentEnergy,
			setCurrentEnergy,
			energyReduction,
			stage,
			soldoTaps,
			setSoldoTaps,
			zecchinoTaps,
			setZecchinoTaps,
		]
	)

	return (
		<div className='tap-zone' onTouchStart={handleTouchStart}>
			<img src={stage === 1 ? silverCoin : goldenCoin} alt='coin' />
		</div>
	)
}

export default TapZone
