import React, { useState } from 'react'
import axiosDB from '../../../utils/axios/axiosConfig'
import Navigation from '../../ui/Navigation/Navigation'
import Loading from '../Loading/Loading'
import './Tasks.css'

const Tasks = () => {
	const [taskData, setTaskData] = useState([])
	const [loading, setLoading] = useState(true)

	const telegramId = '1145622789'
	const getTasks = async () => {
		try {
			const tasksList = axiosDB.get(`/tasks/${telegramId}`)
			setTaskData(tasksList.data)
		} catch (error) {
			console.log(error)
		} finally {
			setLoading(false)
		}

		await getTasks()
	}

	if (loading) {
		return <Loading />
	}
	return (
		<div className='container'>
			<h1 className='main-title'>tasks</h1>
			<div className='tasks-content'>
				{taskData.map((task, index) => (
					<div key={index} id='task-container'>
						<div className='block tasks'>
							<div className='task'>
								<div id='task-block'>
									<div className='task-logo'></div>
									<div id='task-info'>
										<p id='name'>{task.name}</p>
										<p id='reward'>{task.reward}</p>
									</div>
								</div>
								<div className='row icon'></div>
							</div>
						</div>
					</div>
				))}
			</div>
			<Navigation />
		</div>
	)
	/*
	return (
		<div className='container'>
			<h1 className='main-title'>tasks</h1>
			<div className='tasks-content'>
				{taskData.map((task, index) => (
					<div key={index} id='task-container'>
						<div className='block tasks'>
							<div className='task'>
								<div id='task-block'>
									<div className='task-logo'></div>
									<div id='task-info'>
										<p id='name'>{task.name}</p>
										<p id='reward'>{task.reward}</p>
									</div>
								</div>
								<div className='row icon'></div>
							</div>
						</div>
					</div>
				))}
			</div>
			<Navigation />
		</div>
	)
		*/
}

export default Tasks
