import React, { useEffect, useRef } from 'react'
import style from './ProgressBar.module.css' // Убедитесь, что у вас есть CSS файл

const ProgressBar = ({ minTime, maxTime }) => {
	const progressBarRef = useRef(null)

	useEffect(() => {
		if (progressBarRef.current) {
			const now = Date.now()
			const progressPercent = Math.min(
				((now - minTime) / (maxTime - minTime)) * 100,
				100
			)
			progressBarRef.current.style.width = `${progressPercent}%`
		}
	}, [minTime, maxTime])

	return (
		<div className={style.progress_bar_container}>
			<div ref={progressBarRef} className={style.progress_bar}></div>
		</div>
	)
}

export default ProgressBar
