import React, { useEffect, useState } from 'react'
import axiosDB from '../../../utils/axios/axiosConfig'
import Navigation from '../../ui/Navigation/Navigation'
import Loading from '../Loading/Loading'
import TaskWindow from './TaskWindow'
import './Tasks.css'

// Функция для разделения массива на части по заданному размеру
const chunkArray = (array, chunkSize) => {
	const result = []
	for (let i = 0; i < array.length; i += chunkSize) {
		result.push(array.slice(i, i + chunkSize))
	}
	return result
}

const Tasks = () => {
	const [taskData, setTaskData] = useState(null)
	const [selectedTask, setSelectedTask] = useState(null)
	const [loading, setLoading] = useState(true)

	const telegramId = '1145622789'

	useEffect(() => {
		const getTasks = async () => {
			try {
				const response = await axiosDB.get(`/tasks/${telegramId}`)
				setTaskData(response.data)
				console.log('TASKDATA: ', response.data)
			} catch (error) {
				console.error('Error fetching tasks:', error)
			} finally {
				setLoading(false)
			}
		}

		getTasks()
	}, [telegramId])

	const completeTask = async taskId => {
		try {
			await axiosDB.post(`/tasks/complete`, { id: taskId })
			// Обновление состояния задачи после успешного запроса
			setTaskData(prevData => {
				const updatedIncompleteTasks = prevData.incompleteTasks.filter(
					task => task.id !== taskId
				)
				const completedTask = prevData.incompleteTasks.find(
					task => task.id === taskId
				)
				return {
					...prevData,
					incompleteTasks: updatedIncompleteTasks,
					completedTasks: [
						...prevData.completedTasks,
						{ ...completedTask, isComplete: true },
					],
				}
			})
		} catch (error) {
			console.error('Error completing task:', error)
		}
	}

	if (loading) {
		return <Loading />
	}

	// Проверяем наличие данных taskData и не пуст ли массив неполных задач
	if (
		!taskData ||
		!taskData.incompleteTasks ||
		taskData.incompleteTasks.length === 0
	) {
		return (
			<div className='container'>
				<h1 className='main-title'>Tasks</h1>
				<div className='tasks-content'>
					<h1 className='text-center'>Soon...</h1>
				</div>
				<Navigation />
			</div>
		)
	}

	const showTaskWindow = task => {
		setSelectedTask(task)
	}

	const closeTaskWindow = () => {
		setSelectedTask(null)
	}

	const incompleteTaskGroups = chunkArray(taskData.incompleteTasks, 3)
	const completedTaskGroups = chunkArray(taskData.completedTasks, 3)

	return (
		<div className='container'>
			<h1 className='main-title'>Tasks</h1>
			<div className='tasks-content'>
				{incompleteTaskGroups.map((group, groupIndex) => (
					<div key={`incomplete-group-${groupIndex}`} id='task-container'>
						<div className='block tasks'>
							{group.map((task, index) => (
								<div
									key={`incomplete-${groupIndex}-${index}`}
									className='task'
									onClick={() => showTaskWindow(task)}
								>
									<div id='task-block'>
										<div className='task-logo'>
											<img src={task.iconSrc} alt='task' />
										</div>
										<div id='task-info'>
											<p id='name'>{task.taskType}</p>
											<p id='reward'>
												{task.isComplete ? 'Completed' : task.reward}
											</p>
										</div>
									</div>
									<div className='row icon'></div>
								</div>
							))}
						</div>
					</div>
				))}

				{completedTaskGroups.map((group, groupIndex) => (
					<div key={`completed-group-${groupIndex}`} id='task-container'>
						<div className='block tasks'>
							{group.map((task, index) => (
								<div key={`completed-${groupIndex}-${index}`} className='task'>
									<div id='task-block'>
										<div className='task-logo'>
											<img src={task.iconSrc} alt='task' />
										</div>
										<div id='task-info'>
											<p id='name'>{task.taskType}</p>
											<p id='reward'>
												{task.isComplete ? 'Completed' : task.reward}
											</p>
										</div>
									</div>
									<div className='success icon'></div>
								</div>
							))}
						</div>
					</div>
				))}
			</div>
			<Navigation />
			{selectedTask && (
				<TaskWindow
					showTaskWindow={closeTaskWindow}
					taskData={selectedTask}
					buttonText={'Перейти'}
					completeTask={completeTask}
				/>
			)}
		</div>
	)
}

export default Tasks
