import React, { useEffect, useState } from 'react'
import Navigation from '../../ui/Navigation/Navigation'
import Loading from '../Loading/Loading'
import BoostList from './BoostComponents/BoostList'
import BoostMenu from './BoostComponents/BoostMenu'
import DayBoosts from './BoostComponents/DayBoosts'
import { allBoosts } from './BoostData.js'
import './Boosts.css'
import Popup from './Popup/Popup'
import {
	calculateTimeLeft,
	fetchUserData,
	formatTimeLeft,
	updateBoostData,
} from './Utils/helpers'

const Boosts = () => {
	const telegramId = '1145622789'
	const [activeMenuItem, setActiveMenuItem] = useState('SoldoZecchino')
	const [boostData, setBoostData] = useState([])
	const [userData, setUserData] = useState({})
	const [loading, setLoading] = useState(true)
	const [popupInfo, setPopupInfo] = useState({
		title: '',
		iconSrc: '',
		boostType: '',
		name: '',
	})

	useEffect(() => {
		fetchUserData(telegramId, setBoostData, setUserData, setLoading)

		const interval = setInterval(() => {
			setBoostData(prevBoostData => [...prevBoostData])
		}, 60000) // Update every minute

		return () => clearInterval(interval)
	}, [telegramId])

	const handleMenuItemClick = name => setActiveMenuItem(name)

	const handleBoostClick = (
		title,
		iconSrc,
		boostType = 'default-boost',
		name
	) => {
		setPopupInfo({ title, iconSrc, boostType, name })
	}

	const handlePopupClose = () =>
		setPopupInfo({ title: '', iconSrc: '', boostType: '', name: '' })

	const filteredBoosts = allBoosts.filter(
		boost =>
			boost.boostType === activeMenuItem || boost.boostType === 'daily-boost'
	)

	if (loading) return <Loading />

	return (
		<div className='container boosts'>
			{popupInfo.title && (
				<Popup
					handlePopupClose={handlePopupClose}
					popupInfo={popupInfo}
					buttonText={
						popupInfo.boostType !== 'SoldoZecchino' ? 'Get' : 'Upgrade'
					}
					userData={userData}
					updateBoostData={() =>
						updateBoostData(telegramId, setBoostData, setUserData)
					}
				/>
			)}

			<h1 className='main-title'>Boosts</h1>
			<h3 className='post-title'>Free Day Boosts</h3>

			<DayBoosts
				boostData={boostData}
				handleBoostClick={handleBoostClick}
				userData={userData}
				calculateTimeLeft={calculateTimeLeft}
				formatTimeLeft={formatTimeLeft}
			/>

			<h3 className='up-case text-center post-title'>Boosts</h3>

			<BoostMenu
				activeMenuItem={activeMenuItem}
				handleMenuItemClick={handleMenuItemClick}
			/>

			<BoostList
				filteredBoosts={filteredBoosts}
				handleBoostClick={handleBoostClick}
				userData={userData}
			/>

			<Navigation />
		</div>
	)
}

export default Boosts
