import axiosDB from '../../../utils/axios/axiosConfig'

const UpgradeBoostPopup = ({
	handlePopupClose,
	boost,
	userData,
	updateBoostData,
}) => {
	const upgradeBoost = async (boost, userData) => {
		try {
			const boostElem = boost[0]
			const isDaily = boostElem.boostType === 'daily' ? 1 : 0 // Using 1 for daily and 0 for non-daily
			const reqLink = `/upgradeBoost/${userData.telegramId}/${boostElem.name}/${isDaily}`

			const response = await axiosDB.put(reqLink)
			console.log(response)
			if (response.status === 200) {
				updateBoostData()
			}
		} catch (error) {
			console.log(error)
		}
	}

	console.log(boost[0])

	const activateTreeBoost = async () => {
		try {
			const boostElem = boost[0]
			const response = await axiosDB.put(
				`/activateTreeBoost/${userData.telegramId}/${boostElem.name}`
			)
			console.log(response)
			if (response.status === 200) {
				updateBoostData()
			}
		} catch (error) {
			console.log(error)
		}
	}
	return (
		<div className='popup-overlay' onClick={handlePopupClose}>
			<div className='popup block'>
				<div id='boost-info'>
					<div className='popup-icon'>
						<img src={boost[0].icon} alt='boost icon' />
					</div>
					<h3 className='popup-title'>{boost[0].name}</h3>
					{boost[0].boostType !== 'one-time' && (
						<h3 className='boost-level-info'>
							{boost[0].level} â†’ {boost[0].level + 1}
						</h3>
					)}
				</div>
				<button onClick={upgradeBoost} className='gradient-btn'>
					{boost[0].currency !== 'zecchino' ? 'Upgrade' : 'Activate'}
				</button>
			</div>
		</div>
	)
}

export default UpgradeBoostPopup
